                        <td className="px-4 py-2 border">
                          <button
                            onClick={() => openEditDialog(product)}
                            className="bg-blue-500 text-white px-4 py-2 rounded"
                          >
                            Update
                          </button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </section>

            {/* Update Product Dialog */}
            {selectedProduct && (
              <div className="fixed inset-0 z-50 bg-gray-600 bg-opacity-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded shadow-lg w-1/2 max-w-lg">
                  <h2 className="text-2xl font-bold mb-4">
                    Update Product Details
                  </h2>

                  {/* Form Fields */}
                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Title</label>
                    <input
                      type="text"
                      value={formValues.title}
                      onChange={(e) =>
                        setFormValues({ ...formValues, title: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Description
                    </label>
                    <input
                      type="text"
                      value={formValues.description}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          description: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Price</label>
                    <input
                      type="number"
                      value={formValues.price}
                      onChange={(e) =>
                        setFormValues({ ...formValues, price: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Expiry Date
                    </label>
                    <input
                      type="date"
                      value={formValues.expiry_date}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          expiry_date: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Quantity</label>
                    <input
                      type="number"
                      value={formValues.quantity}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          quantity: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Type</label>
                    <input
                      type="text"
                      value={formValues.type}
                      onChange={(e) =>
                        setFormValues({ ...formValues, type: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Manufacturing Date
                    </label>
                    <input
                      type="date"
                      value={formValues.mfg_date}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          mfg_date: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  {/* Submit Button */}
                  <button
                    onClick={handleUpdateSubmit}
                    className="mt-6 bg-blue-500 text-white px-4 py-2 rounded"
                  >
                    Submit
                  </button>

                  {/* Close Button */}
                  <button
                    onClick={() => setSelectedProduct(null)}
                    className="mt-4 bg-gray-400 text-white px-4 py-2 rounded"
                  >
                    Close
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
      </section>
    </>
  );
};

export default Admin;
