import React, { useState, useEffect } from "react";
import useFirebase from "../hooks/useFirebase";
import "./admin.css";

const Admin = () => {
  const {
    user,
    isLoading,
    getMedicines,
    getOrders,
    getMessages,
  } = useFirebase();
  const [orders, setOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [sales, setSales] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedTab, setSelectedTab] = useState("All Orders");

  useEffect(() => {
    if (user) {
      fetchData();
    }
  }, [user]);

  const fetchData = async () => {
    // Fetch all orders and sort by timestamp
    const fetchedOrders = await getOrders();
    setOrders(fetchedOrders);

    // Fetch other data (Medicines, Suppliers, Sales, Messages)
    setMessages(await getMessages());
  };

  const handleOrderStatusUpdate = (orderId, newStatus) => {
    console.log(`Updating order ${orderId} to ${newStatus}`);
    // Implement Firebase update logic here to update the order status
  };

  return (
    <>
      <section className="admin-section px-8 py-12">
        <h1 className="text-3xl font-bold text-gray-700 mb-8 mt-10">Admin Dashboard</h1>

        {/* Tabs for Admin Sections */}
        <div className="flex space-x-4 mb-8">
          {[
            "All Orders",
            "Pending Orders",
            "Dispatched Orders",
            "Delivered Orders"
          ].map((tab) => (
            <button
              key={tab}
              className={`px-4 py-2 rounded ${
                selectedTab === tab ? "bg-blue-500 text-white" : "bg-gray-200"
              }`}
              onClick={() => setSelectedTab(tab)}
            >
              {tab}
            </button>
          ))}
        </div>

        {selectedTab === "All Orders" && (
          <section>
            <h2 className="text-xl font-semibold text-gray-700 mb-4">All Orders</h2>
            <table className="table-auto w-full text-left border-collapse">
              <thead>
                <tr>
                  <th className="px-4 py-2 border">Order ID</th>
                  <th className="px-4 py-2 border">Bill</th>
                  <th className="px-4 py-2 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order.id}>
                    <td className="px-4 py-2 border">{order.id}</td>
                    <td className="px-4 py-2 border">{order.bill}</td>
                    <td className="px-4 py-2 border">
                      <select
                        onChange={(e) =>
                          handleOrderStatusUpdate(order.id, e.target.value)
                        }
                        value={order.status}
                        className="border p-1 rounded"
                      >
                        <option value="Pending">Pending</option>
                        <option value="Dispatched">Dispatched</option>
                        <option value="Delivered">Delivered</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </section>
        )}

        {selectedTab === "Pending Orders" && (
          <section>
            <h2 className="text-xl font-semibold text-gray-700 mb-4">Pending Orders</h2>
            <table className="table-auto w-full text-left border-collapse">
              <thead>
                <tr>
                  <th className="px-4 py-2 border">Order ID</th>
                  <th className="px-4 py-2 border">Bill</th>
                  <th className="px-4 py-2 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {orders
                  .filter((order) => order.status === "Pending")
                  .map((order) => (
                    <tr key={order.id}>
                      <td className="px-4 py-2 border">{order.id}</td>
                      <td className="px-4 py-2 border">{order.bill}</td>
                      <td className="px-4 py-2 border">
                        <select
                          onChange={(e) =>
                            handleOrderStatusUpdate(order.id, e.target.value)
                          }
                          value={order.status}
                          className="border p-1 rounded"
                        >
                          <option value="Pending">Pending</option>
                          <option value="Dispatched">Dispatched</option>
                          <option value="Delivered">Delivered</option>
                        </select>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </section>
        )}

        {selectedTab === "Dispatched Orders" && (
          <section>
            <h2 className="text-xl font-semibold text-gray-700 mb-4">Dispatched Orders</h2>
            <table className="table-auto w-full text-left border-collapse">
              <thead>
                <tr>
                  <th className="px-4 py-2 border">Order ID</th>
                  <th className="px-4 py-2 border">Bill</th>
                  <th className="px-4 py-2 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {orders
                  .filter((order) => order.status === "Dispatched")
                  .map((order) => (
                    <tr key={order.id}>
                      <td className="px-4 py-2 border">{order.id}</td>
                      <td className="px-4 py-2 border">{order.bill}</td>
                      <td className="px-4 py-2 border">
                        <select
                          onChange={(e) =>
                            handleOrderStatusUpdate(order.id, e.target.value)
                          }
                          value={order.status}
                          className="border p-1 rounded"
                        >
                          <option value="Pending">Pending</option>
                          <option value="Dispatched">Dispatched</option>
                          <option value="Delivered">Delivered</option>
                        </select>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </section>
        )}

        {selectedTab === "Delivered Orders" && (
          <section>
            <h2 className="text-xl font-semibold text-gray-700 mb-4">Delivered Orders</h2>
            <table className="table-auto w-full text-left border-collapse">
              <thead>
                <tr>
                  <th className="px-4 py-2 border">Order ID</th>
                  <th className="px-4 py-2 border">Bill</th>
                  <th className="px-4 py-2 border">Status</th>
                </tr>
              </thead>
              <tbody>
                {orders
                  .filter((order) => order.status === "Delivered")
                  .map((order) => (
                    <tr key={order.id}>
                      <td className="px-4 py-2 border">{order.id}</td>
                      <td className="px-4 py-2 border">{order.bill}</td>
                      <td className="px-4 py-2 border">
                        <select
                          onChange={(e) =>
                            handleOrderStatusUpdate(order.id, e.target.value)
                          }
                          value={order.status}
                          className="border p-1 rounded"
                        >
                          <option value="Pending">Pending</option>
                          <option value="Dispatched">Dispatched</option>
                          <option value="Delivered">Delivered</option>
                        </select>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </section>
        )}
      </section>
    </>
  );
};

export default Admin;
