import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Table, Select, Button, Tabs, TabPanel, TabList, Tab } from '@chakra-ui/react';
import { useFirestore } from '../hooks/useFirebase'; // Custom hook to fetch data from Firestore

const Admin = () => {
    const { getMedicines, getOrders, getSuppliers, getSales } = useFirestore(); // Custom hook for Firestore access
    const [medicines, setMedicines] = useState([]);
    const [orders, setOrders] = useState([]);
    const [suppliers, setSuppliers] = useState([]);
    const [sales, setSales] = useState([]);
    
    // Fetch data on mount
    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        setMedicines(await getMedicines());
        setOrders(await getOrders());
        setSuppliers(await getSuppliers());
        setSales(await getSales());
    };

    // Data for quantity statistics chart
    const quantityData = {
        labels: medicines.map(med => med.name),
        datasets: [{
            label: 'Quantity',
            data: medicines.map(med => med.quantity),
            backgroundColor: 'rgba(75,192,192,0.6)'
        }]
    };

    // Sort medicines by expiration date and take the 5 nearest
    const expiringSoon = [...medicines]
        .sort((a, b) => new Date(a.expirationDate) - new Date(b.expirationDate))
        .slice(0, 5);

    // Handle order status update
    const handleOrderStatusUpdate = (orderId, newStatus) => {
        // Call Firestore to update order status
        console.log(`Updating order ${orderId} to ${newStatus}`);
    };

    // Handle quantity update
    const handleQuantityUpdate = (id, newQuantity) => {
        // Update quantity in Firestore
        console.log(`Updating medicine ${id} to quantity ${newQuantity}`);
    };

    return (
        <section className="admin-section px-8 py-12">
            <h1 className="text-3xl font-bold text-gray-700 mb-8">Admin Dashboard</h1>
            
            {/* Statistics Section */}
            <section className="mb-12">
                <h2 className="text-xl font-semibold text-gray-700 mb-4">Statistics</h2>
                <Bar data={quantityData} />
            </section>

            {/* Expiring Soon Section */}
            <section className="mb-12">
                <h2 className="text-xl font-semibold text-gray-700 mb-4">Expiring Soon</h2>
                <Table variant="striped" colorScheme="gray">
                    <thead>
                        <tr>
                            <th>Medicine</th>
                            <th>Expiration Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {expiringSoon.map(med => (
                            <tr key={med.id}>
                                <td>{med.name}</td>
                                <td>{med.expirationDate}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </section>

            {/* Order Status Section */}
            <Tabs variant="enclosed" colorScheme="blue" mb={12}>
                <TabList>
                    <Tab>Order Status</Tab>
                    <Tab>Suppliers</Tab>
                    <Tab>Sales History</Tab>
                    <Tab>Product Management</Tab>
                </TabList>

                {/* Order Status Tab */}
                <TabPanel>
                    <h2 className="text-xl font-semibold text-gray-700 mb-4">Order Status</h2>
                    <Table variant="striped" colorScheme="gray">
                        <thead>
                            <tr>
                                <th>Order ID</th>
                                <th>Status</th>
                                <th>Update Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders.map(order => (
                                <tr key={order.id}>
                                    <td>{order.id}</td>
                                    <td>{order.status}</td>
                                    <td>
                                        <Select
                                            placeholder="Select status"
                                            onChange={(e) => handleOrderStatusUpdate(order.id, e.target.value)}
                                        >
                                            <option value="Pending">Pending</option>
                                            <option value="Shipped">Shipped</option>
                                            <option value="Delivered">Delivered</option>
                                        </Select>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </TabPanel>

                {/* Suppliers Tab */}
                <TabPanel>
                    <h2 className="text-xl font-semibold text-gray-700 mb-4">Suppliers</h2>
                    <Table variant="striped" colorScheme="gray">
                        <thead>
                            <tr>
                                <th>Supplier</th>
                                <th>Order</th>
                            </tr>
                        </thead>
                        <tbody>
                            {suppliers.map(supplier => (
                                <tr key={supplier.id}>
                                    <td>{supplier.name}</td>
                                    <td>
                                        <Button colorScheme="blue" onClick={() => console.log(`Order placed with ${supplier.name}`)}>
                                            Place Order
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </TabPanel>

                {/* Sales History Tab */}
                <TabPanel>
                    <h2 className="text-xl font-semibold text-gray-700 mb-4">Sales History</h2>
                    <Table variant="striped" colorScheme="gray">
                        <thead>
                            <tr>
                                <th>Transaction ID</th>
                                <th>Medicine</th>
                                <th>Quantity</th>
                                <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sales.map(sale => (
                                <tr key={sale.id}>
                                    <td>{sale.transactionId}</td>
                                    <td>{sale.medicineName}</td>
                                    <td>{sale.quantity}</td>
                                    <td>{sale.date}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </TabPanel>

                {/* Product Management Tab */}
                <TabPanel>
                    <h2 className="text-xl font-semibold text-gray-700 mb-4">Product Management</h2>
                    <Table variant="striped" colorScheme="gray">
                        <thead>
                            <tr>
                                <th>Medicine</th>
                                <th>Quantity</th>
                                <th>Availability</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {medicines.map(med => (
                                <tr key={med.id}>
                                    <td>{med.name}</td>
                                    <td>
                                        <input
                                            type="number"
                                            value={med.quantity}
                                            onChange={(e) => handleQuantityUpdate(med.id, e.target.value)}
                                            className="border rounded p-1"
                                        />
                                    </td>
                                    <td>{med.isAvailable ? 'Available' : 'Not Available'}</td>
                                    <td>
                                        <Button
                                            colorScheme="blue"
                                            onClick={() => console.log(`Toggling availability for ${med.id}`)}
                                        >
                                            {med.isAvailable ? 'Disable' : 'Enable'}
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </TabPanel>
            </Tabs>
        </section>
    );
};

export default Admin;
