import React, { useState } from 'react';
import Bounce from 'react-reveal/Bounce';
import { Box, Button, FormControl, FormLabel, Input, Radio, RadioGroup, Select, Stack, Text, Image } from '@chakra-ui/react';
import OrderCard from '../components/Order/OrderCard';
import useOrder from '../hooks/useOrder';
import { AnimatePresence } from 'framer-motion';

const OrderScreen = () => {
    const { orders } = useOrder();
    const [deliveryTime, setDeliveryTime] = useState('');
    const [recurringOrder, setRecurringOrder] = useState('');
    const [paymentType, setPaymentType] = useState('');
    const [upiScreenshot, setUpiScreenshot] = useState(null);

    const handleScreenshotUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            setUpiScreenshot(URL.createObjectURL(file));
        }
    };

    const handlePlaceOrder = () => {
        // Logic to place the order with the given details
        console.log({
            deliveryTime,
            recurringOrder,
            paymentType,
            upiScreenshot,
        });
        alert("Order placed successfully!");
    };

    return (
        <section className="max-w-screen-xl py-24 mx-auto px-6">
            {orders.length === 0 ? (
                <div className="h-screen">
                    <h1 className="text-4xl poppins text-center text-blue-600">Cart is Empty!</h1>
                </div>
            ) : (
                <>
                    <Bounce left>
                        <div className="flex flex-col items-center space-x-2 pb-8">
                            <h1 className="text-gray-700 poppins text-3xl">
                                All <span className="text-blue-600 font-semibold select-none">Orders</span>
                            </h1>
                            <div className="bg-blue-600 flex items-center justify-center w-16 h-1 mt-2 rounded-full"></div>
                        </div>
                    </Bounce>
                    <div className="flex justify-center">
                        <div className="flex flex-col space-y-4">
                            {orders.map((item) => (
                                <OrderCard key={item.id} {...item} />
                            ))}
                        </div>
                    </div>
                    
                    {/* Order Details Form */}
                    <Box mt={10} p={5} borderWidth="1px" borderRadius="lg" shadow="md" bg="white">
                        <Text fontSize="2xl" fontWeight="bold" mb={5}>
                            Order Details
                        </Text>
                        
                        {/* Delivery Timing */}
                        <FormControl mb={4}>
                            <FormLabel>Delivery Timing</FormLabel>
                            <Select placeholder="Select delivery timing" onChange={(e) => setDeliveryTime(e.target.value)}>
                                <option value="Morning">Morning</option>
                                <option value="Afternoon">Afternoon</option>
                                <option value="Evening">Evening</option>
                            </Select>
                        </FormControl>

                        {/* Recurring Order */}
                        <FormControl mb={4}>
                            <FormLabel>Recurring Order</FormLabel>
                            <Select placeholder="Select recurrence" onChange={(e) => setRecurringOrder(e.target.value)}>
                                <option value="Every Month">Every Month</option>
                                <option value="Every 2 Months">Every 2 Months</option>
                                <option value="One-time">One-time</option>
                            </Select>
                        </FormControl>

                        {/* Payment Type */}
                        <FormControl mb={4}>
                            <FormLabel>Payment Type</FormLabel>
                            <RadioGroup onChange={setPaymentType} value={paymentType}>
                                <Stack direction="row">
                                    <Radio value="Cash">Cash</Radio>
                                    <Radio value="UPI">UPI</Radio>
                                </Stack>
                            </RadioGroup>
                        </FormControl>

                        {/* UPI QR Code and Screenshot Upload */}
                        {paymentType === 'UPI' && (
                            <Box mt={4}>
                                <Text fontSize="md" mb={2}>Scan QR Code to Pay</Text>
                                <Image src="/path-to-your-qr-code-image.png" alt="UPI QR Code" boxSize="150px" mb={4} />
                                
                                <FormControl>
                                    <FormLabel>Upload Screenshot</FormLabel>
                                    <Input type="file" onChange={handleScreenshotUpload} />
                                </FormControl>
                                
                                {upiScreenshot && (
                                    <Image src={upiScreenshot} alt="UPI Screenshot" boxSize="100px" mt={4} />
                                )}
                            </Box>
                        )}

                        {/* Place Order Button */}
                        <Button colorScheme="blue" mt={6} onClick={handlePlaceOrder}>
                            Place Order
                        </Button>
                    </Box>
                </>
            )}
        </section>
    );
};

export default OrderScreen;
