import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import { getFirestore, doc, setDoc, getDoc } from 'firebase/firestore';
import useAuth from '../hooks/useAuth';

const ProfilePage = () => {
    const { user } = useAuth();
    const history = useHistory();
    const db = getFirestore();

    const [profileData, setProfileData] = useState({
        phone: '',
        bloodGroup: '',
        rollOrEmpId: '',
        age: '',
        medicalBio: '',
    });

    useEffect(() => {
        const fetchProfileData = async () => {
            if (user) {
                const userDoc = doc(db, "user_profile", user.uid);
                const docSnap = await getDoc(userDoc);
                if (docSnap.exists()) {
                    setProfileData(docSnap.data());
                }
            }
        };
        fetchProfileData();
    }, [user, db]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfileData(prev => ({ ...prev, [name]: value }));
    };

    const handleSave = async (e) => {
        e.preventDefault();
        if (user) {
            const userDoc = doc(db, "user_profile", user.uid);
            await setDoc(userDoc, {
                name: user.displayName,
                email: user.email,
                ...profileData
            });
            alert("Profile updated successfully");
        }
    };

    return (
        <div className="profile-page">
            <header>
                {/* Navbar */}
            </header>
            <main className="container mx-auto p-4">
                <h1>Profile Page</h1>
                <form onSubmit={handleSave}>
                    <p><strong>Name:</strong> {user.displayName}</p>
                    <p><strong>Email:</strong> {user.email}</p>
                    <label>
                        Phone Number:
                        <input type="text" name="phone" value={profileData.phone} onChange={handleChange} />
                    </label>
                    <label>
                        Blood Group:
                        <input type="text" name="bloodGroup" value={profileData.bloodGroup} onChange={handleChange} />
                    </label>
                    <label>
                        Roll No/Emp ID:
                        <input type="text" name="rollOrEmpId" value={profileData.rollOrEmpId} onChange={handleChange} />
                    </label>
                    <label>
                        Age:
                        <input type="number" name="age" value={profileData.age} onChange={handleChange} />
                    </label>
                    <label>
                        Medical Bio:
                        <textarea name="medicalBio" value={profileData.medicalBio} onChange={handleChange} />
                    </label>
                    <button type="submit">Save Profile</button>
                </form>
            </main>
            <footer>
                {/* Footer */}
            </footer>
        </div>
    );
};

export default ProfilePage;
