import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Assuming you are using react-router
import { getFirestore, doc, setDoc, getDoc } from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import useAuth from '../hooks/useAuth';

const allowedEmails = ["b22es006@iitj.ac.in", "b22cs101@iitj.ac.in", "b22cs014@iitj.ac.in"];

const ProfilePage = () => {
    const { user, setUser } = useAuth();
    const db = getFirestore();
    const storage = getStorage();
    const navigate = useNavigate();

    const [profileData, setProfileData] = useState({
        phone: '',
        bloodGroup: '',
        rollOrEmpId: '',
        age: '',
        medicalBio: '',
    });
    const [profilePic, setProfilePic] = useState(user.photoURL || "https://cdn-icons-png.flaticon.com/512/236/236832.png");
    const [imageFile, setImageFile] = useState(null);

    useEffect(() => {
        const fetchProfileData = async () => {
            if (user) {
                const userDoc = doc(db, "user_profile", user.uid);
                const docSnap = await getDoc(userDoc);
                if (docSnap.exists()) {
                    setProfileData(docSnap.data());
                }
            }
        };
        fetchProfileData();
    }, [user, db]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfileData(prev => ({ ...prev, [name]: value }));
    };

    const handleImageChange = (e) => {
        if (e.target.files[0]) {
            setImageFile(e.target.files[0]);
        }
    };

    const handleSave = async (e) => {
        e.preventDefault();
        if (user) {
            let imageUrl = profilePic;

            // Upload the image to Firebase Storage if a new image is selected
            if (imageFile) {
                const storageRef = ref(storage, `profile_pictures/${user.uid}`);
                await uploadBytes(storageRef, imageFile);
                imageUrl = await getDownloadURL(storageRef);

                // Update the profile picture in Firebase Auth
                await updateProfile(auth.currentUser, { photoURL: imageUrl });
                setProfilePic(imageUrl); // Update local profile picture display
            }

            // Save profile data to Firestore
            const userDoc = doc(db, "user_profile", user.uid);
            await setDoc(userDoc, {
                name: user.displayName,
                email: user.email,
                photoURL: imageUrl,
                ...profileData
            });

            alert("Profile updated successfully");
        }
    };

    const handleAdminAccess = () => {
        navigate('/admin'); // Redirects to the admin page
    };

    return (
        <div className="profile-page container mx-auto p-4 mt-20">
            <header className="flex justify-between items-start mb-6">
                <h1 className="text-3xl font-bold">Profile</h1>
                <div>
                    <img
                        src={profilePic}
                        alt="Profile"
                        className="w-20 h-20 rounded-full border mb-2"
                    />
                    <input type="file" accept="image/*" onChange={handleImageChange} className="mt-2" />
                </div>
            </header>

            {/* Admin Access Button */}
            {allowedEmails.includes(user.email) && (
                <button 
                    onClick={handleAdminAccess}
                    className="bg-green-500 text-white py-2 px-4 rounded-lg font-semibold hover:bg-green-600 mb-4"
                >
                    Admin Access
                </button>
            )}

            <form onSubmit={handleSave} className="bg-white p-6 rounded-lg shadow-lg">
                <div className="grid grid-cols-1 gap-4 mb-6">
                    <div>
                        <label className="font-semibold">Name:</label>
                        <p>{user.displayName}</p>
                    </div>
                    <div>
                        <label className="font-semibold">Email:</label>
                        <p>{user.email}</p>
                    </div>
                    <div>
                        <label className="font-semibold">Phone Number:</label>
                        <input 
                            type="text"
                            name="phone"
                            value={profileData.phone}
                            onChange={handleChange}
                            maxLength="10"
                            className="w-full p-2 border rounded"
                        />
                    </div>
                    <div>
                        <label className="font-semibold">Blood Group:</label>
                        <input 
                            type="text"
                            name="bloodGroup"
                            value={profileData.bloodGroup}
                            onChange={handleChange}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                    <div>
                        <label className="font-semibold">Roll No/Emp ID:</label>
                        <input 
                            type="text"
                            name="rollOrEmpId"
                            value={profileData.rollOrEmpId}
                            onChange={handleChange}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                    <div>
                        <label className="font-semibold">Age:</label>
                        <input 
                            type="number"
                            name="age"
                            value={profileData.age}
                            onChange={handleChange}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                    <div>
                        <label className="font-semibold">Medical Bio:</label>
                        <textarea 
                            name="medicalBio"
                            value={profileData.medicalBio}
                            onChange={handleChange}
                            className="w-full p-2 border rounded"
                        />
                    </div>
                </div>
                
                <div className="text-center">
                    <button 
                        type="submit"
                        className="bg-blue-500 text-white py-2 px-6 rounded-lg font-semibold hover:bg-blue-600"
                    >
                        Update Profile
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ProfilePage;
