import React, { useState, useEffect } from "react";
import useFirebase from "../hooks/useFirebase";
import "./admin.css";

const Admin = () => {
  const {
    user,
    isLoading,
    getMedicines,
    getOrders,
    getMessages,
  } = useFirebase();
  const [orders, setOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [sales, setSales] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedTab, setSelectedTab] = useState("All Orders");

  useEffect(() => {
    if (user) {
      fetchData();
    }
  }, [user]);

  const fetchData = async () => {
    // Fetch all orders and sort by timestamp
    const fetchedOrders = await getOrders();
    setOrders(fetchedOrders);

    // Fetch other data (Medicines, Suppliers, Sales, Messages)
    setMessages(await getMessages());
  };

  const handleOrderStatusUpdate = (orderId, newStatus) => {
    console.log(`Updating order ${orderId} to ${newStatus}`);
    // Implement Firebase update logic here to update the order status
  };

  return (
    <>
      <section className="admin-section px-8 py-12">
        <h1 className="text-3xl font-bold text-gray-700 mb-8 mt-10">Admin Dashboard</h1>

        {/* Tabs for Admin Sections */}
        <div className="flex space-x-4 mb-8">
          {[
            "All Orders",
            "Pending Orders",
            "Dispatched Orders",
            "Delivered Orders",
            "Messages"
