import { useState, useEffect } from 'react';
import { auth } from '../config/firebase'; // Adjust this import path if necessary

const useAuth = () => {
    const [user, setUser] = useState(null);

    // Google Sign-In logic (assuming you have it)
    const googleSignIn = async () => {
        // Handle Google sign-in and set `user` here
    };

    // Email/Password Sign-In
    const signInUser = async (email, password) => {
        try {
            const response = await auth.signInWithEmailAndPassword(email, password);
            setUser(response.user); // Set user data here
        } catch (error) {
            console.error("Error signing in:", error);
        }
    };

    // Sign Out
    const signOutUser = async () => {
        await auth.signOut();
        setUser(null);
    };

    return { user, signInUser, googleSignIn, signOutUser };
};

export default useAuth;
