// src/routes/AdminRoute.js
import React from 'react';
import { Navigate } from 'react-router-dom'; // Use Navigate instead of Redirect
import { useAuth } from '../hooks/useAuth'; // Custom hook to get user info

const AdminRoute = ({ children }) => {
  const { user } = useAuth();
  const allowedEmails = ['b22es006@iitj.ac.in', 'b22cs101@iitj.ac.in', 'b22cs014@iitj.ac.in'];

  // If the user is not in the allowed list, redirect to home
  if (!user || !allowedEmails.includes(user.email)) {
    return <Navigate to="/" />; // Redirect to home page
  }

  return children; // If the user is allowed, render the children (AdminPage)
};

export default AdminRoute;
