import React from 'react';
import { BsCart2 } from 'react-icons/bs';
import { FiLogOut } from 'react-icons/fi';
import { useHistory } from 'react-router-dom';
import useAuth from '../../hooks/useAuth';
import useOrder from '../../hooks/useOrder';

const AuthorizeUser = () => {
    const { user, signOutUser } = useAuth();
    const history = useHistory();
    const { orders } = useOrder();

    return (
        <>
            {user ? (
                <div className="flex items-center justify-end space-x-4">
                    {/* Cart with Order Count */}
                    <div className="relative flex cursor-pointer" onClick={() => history.push('/orders')}>
                        <span className="bg-blue-600 w-6 h-6 rounded-full flex items-center justify-center text-white poppins absolute -right-2 -top-2">
                            {orders.length}
                        </span>
                        <BsCart2 className="cursor-pointer w-6 h-6 text-gray-700" />
                    </div>

                    {/* User Profile Image */}
                    <img
                        src={
                            user.photoURL && user.photoURL.endsWith('g') 
                                ? user.photoURL 
                                : 'https://cdn-icons-png.flaticon.com/512/236/236832.png'
                        }
                        alt={user.displayName || 'User'}
                        className="w-10 h-10 rounded-full"
                    />

                    {/* Display Name */}
                    <p className="text-gray-700 poppins hidden md:block lg:block">
                        {user.displayName || 'User'}
                    </p>

                    {/* Logout Icon */}
                    <FiLogOut className="cursor-pointer w-6 h-6 text-gray-700" onClick={signOutUser} />
                </div>
            ) : (
                <div className="flex items-center justify-end space-x-6">
                    {/* Sign-In and Sign-Up Buttons */}
                    <button className="poppins" onClick={() => history.push('/signin')}>Sign In</button>
                    <button className="btn-primary px-6 py-3 rounded-full" onClick={() => history.push('/signup')}>Sign Up</button>
                </div>
            )}
        </>
    );
};

export default AuthorizeUser;
