import { useState, useEffect } from 'react';
import { auth } from '../firebase/firebaseConfig'; // import the initialized auth instance
import { onAuthStateChanged, signOut } from '../config/firebase';

const useFirebase = () => {
    const [user, setUser] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Set up an observer on the Auth object to listen for auth state changes
        const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
            setUser(currentUser); // Set the user state when the user is authenticated
            setIsLoading(false);
        });

        // Cleanup on component unmount
        return () => unsubscribe();
    }, []);

    const signOutUser = async () => {
        await signOut(auth);
        setUser(null);
    };

    return {
        user,
        isLoading,
        signOutUser,
    };
};

export default useFirebase;
