        {/* Dialog for responding to a review */}
        {selectedReview && (
          <div className="fixed inset-0 z-50 bg-gray-600 bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-8 rounded shadow-lg w-1/2 max-w-lg">
              <h2 className="text-2xl font-bold mb-4">Respond to Review</h2>

              <p>
                <strong>Username:</strong> {selectedReview.username}
              </p>
              <p>
                <strong>Review:</strong> {selectedReview.review}
              </p>

              {/* Textbox to edit or add response */}
              <textarea
                value={responseMessage}
                onChange={(e) => setResponseMessage(e.target.value)}
                placeholder="Write your response here"
                className="w-full p-2 border mt-4"
                rows="4"
              />

              <div className="mt-4 flex justify-end">
                <button
                  onClick={handleResponseSubmit}
                  className="bg-blue-500 text-white px-6 py-2 rounded"
                >
                  Submit Response
                </button>
                <button
                  onClick={() => setSelectedReview(null)}
                  className="ml-2 bg-gray-500 text-white px-6 py-2 rounded"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        )}

        {selectedTab === "Products" && (
          <div>
            {/* Products Table */}
            <section>
              <h2 className="text-xl font-semibold text-gray-700 mb-4">
                Products
              </h2>
              <input
                type="text"
                placeholder="Search by product name"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="border px-4 py-2 mb-4"
              />

              <table className="table-auto w-full text-left border-collapse">
                <thead>
                  <tr>
                    <th className="px-4 py-2 border">Title</th>
                    <th className="px-4 py-2 border">Expiry Date</th>
                    <th className="px-4 py-2 border">Price</th>
                    <th className="px-4 py-2 border">Quantity</th>
                    <th className="px-4 py-2 border">MFG Date</th>
                    <th className="px-4 py-2 border">Action</th>
                  </tr>
                </thead>
                <tbody>
                  {products
                    .filter((product) =>
                      product.title
                        .toLowerCase()
                        .includes(searchQuery.toLowerCase())
                    )
                    .map((product) => (
                      <tr key={product.id}>
                        <td className="px-4 py-2 border">{product.title}</td>
                        <td className="px-4 py-2 border">
                          {product.expiry_date}
                        </td>
                        <td className="px-4 py-2 border">{product.price}</td>
                        <td className="px-4 py-2 border">{product.quantity}</td>
                        <td className="px-4 py-2 border">{product.mfg_date}</td>
                        <td className="px-4 py-2 border">
                          <button
                            onClick={() => openEditDialog(product)}
                            className="bg-blue-500 text-white px-4 py-2 rounded"
                          >
                            Update
                          </button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </section>

            {/* Update Product Dialog */}
            {selectedProduct && (
              <div className="fixed inset-0 z-50 bg-gray-600 bg-opacity-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded shadow-lg w-1/2 max-w-lg">
                  <h2 className="text-2xl font-bold mb-4">
                    Update Product Details
                  </h2>

                  {/* Form Fields */}
                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Title</label>
                    <input
                      type="text"
                      value={formValues.title}
                      onChange={(e) =>
                        setFormValues({ ...formValues, title: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Description
                    </label>
                    <input
                      type="text"
                      value={formValues.description}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          description: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Price</label>
                    <input
                      type="number"
                      value={formValues.price}
                      onChange={(e) =>
                        setFormValues({ ...formValues, price: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Expiry Date
                    </label>
                    <input
                      type="date"
                      value={formValues.expiry_date}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          expiry_date: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Quantity</label>
                    <input
                      type="number"
                      value={formValues.quantity}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          quantity: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">Type</label>
                    <input
                      type="text"
                      value={formValues.type}
                      onChange={(e) =>
                        setFormValues({ ...formValues, type: e.target.value })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block font-semibold mb-2">
                      Manufacturing Date
                    </label>
                    <input
                      type="date"
                      value={formValues.mfg_date}
                      onChange={(e) =>
                        setFormValues({
                          ...formValues,
                          mfg_date: e.target.value,
                        })
                      }
                      className="w-full px-4 py-2 border rounded"
                    />
                  </div>

                  {/* Submit Button */}
                  <button
                    onClick={handleUpdateSubmit}
                    className="mt-6 bg-blue-500 text-white px-4 py-2 rounded"
                  >
                    Submit
                  </button>

                  {/* Close Button */}
                  <button
                    onClick={() => setSelectedProduct(null)}
                    className="mt-4 bg-gray-400 text-white px-4 py-2 rounded"
                  >
                    Close
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
        {selectedTab === "Expiring Soon" && (
        <section className="mb-12">
        <h2 className="text-xl font-semibold text-gray-700 mb-4">
          Expiring Soon
        </h2>
        <table className="table-auto w-full text-left border-collapse">
          <thead>
            <tr>
              <th className="px-4 py-2 border">Medicine</th>
              <th className="px-4 py-2 border">Expiration Date</th>
            </tr>
          </thead>
          <tbody>
            {medicines.map((med) => (
              <tr key={med.id}>
                <td className="px-4 py-2 border">{med.title}</td>
                <td className="px-4 py-2 border">{med.expiry_date}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
        )}
        {selectedTab === "Contact" && (
          <div className="p-6">
          <h2 className="text-3xl font-bold text-center text-gray-700 mb-6">Contact Messages</h2>
          <table className="min-w-full bg-white border border-gray-300 shadow-md rounded-lg">
              <thead>
                  <tr className="bg-gray-100">
                      <th className="py-2 px-4 border-b">Full Name</th>
                      <th className="py-2 px-4 border-b">Email</th>
                      <th className="py-2 px-4 border-b">Phone Number</th>
                      <th className="py-2 px-4 border-b">Message</th>
                      <th className="py-2 px-4 border-b">Date Submitted</th>
                  </tr>
              </thead>
              <tbody>
                  {contactData.length === 0 ? (
                      <tr>
                          <td colSpan="5" className="text-center py-4">No contact messages available</td>
                      </tr>
                  ) : (
                      contactData.map((contact) => (
                          <tr key={contact.id}>
                              <td className="py-2 px-4 border-b">{contact.fullName}</td>
                              <td className="py-2 px-4 border-b">{contact.email}</td>
                              <td className="py-2 px-4 border-b">{contact.phoneNumber}</td>
                              <td className="py-2 px-4 border-b">{contact.message}</td>
                              <td className="py-2 px-4 border-b">
                                  {/* Convert the timestamp to a readable format */}
                                  {new Date(contact.createdAt.seconds * 1000).toLocaleDateString('en-US')}
                              </td>
                          </tr>
                      ))
                  )}
              </tbody>
          </table>
      </div>
        )}
      </section>
    </>
  );
};

export default Admin;
