import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore"; // Import getDocs for fetching data
import { getAuth } from "firebase/auth";

// Firebase configuration
const firebaseConfig = {
    apiKey: process.env.REACT_APP_API_KEY,
    authDomain: process.env.REACT_APP_AUTH_DOMAIN,
    projectId: process.env.REACT_APP_PROJECT_ID,
    storageBucket: process.env.REACT_APP_STORAGE_BUCKET,
    messagingSenderId: process.env.REACT_APP_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_APP_ID
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Function to initialize Authentication
const initializeAuthentication = () => {
    return getAuth(app);
};

// Initialize Firestore and Authentication
const db = getFirestore(app);
const auth = initializeAuthentication();

// Export db, auth, and Firestore helpers (e.g., collection, getDocs)
export { db, auth, collection, getDocs };
export default initializeAuthentication;
