// export default Testimonial
import React, { useState, useEffect } from 'react';
import { getFirestore, doc, getDoc } from 'firebase/firestore';
import Testimonial from './Testimonial'; // Import Testimonial component

const TestimonialPage = () => {
    const [testimonialData, setTestimonialData] = useState(null);
    const db = getFirestore();

    useEffect(() => {
        const fetchTestimonialData = async () => {
            const userId = 'USER_ID_HERE'; // Replace with dynamic user ID
            const userDocRef = doc(db, 'user_profile', userId);
            const docSnap = await getDoc(userDocRef);

            if (docSnap.exists()) {
                const userData = docSnap.data();
                setTestimonialData({
                    username: userData.name,
                    image: userData.photoURL || "https://cdn-icons-png.flaticon.com/512/236/236832.png",
                    review: userData.serviceReview || "No review provided",
                    rating: userData.rating || 0,  // Assuming there's a rating field in the document
                });
            } else {
                console.log('No such document!');
            }
        };

        fetchTestimonialData();
    }, [db]);

    return (
        <div className="testimonial-page">
            {testimonialData ? (
                <Testimonial
                    username={testimonialData.username}
                    image={testimonialData.image}
                    review={testimonialData.review}
                    rating={testimonialData.rating}
                />
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default TestimonialPage;
