import React, { useState, useEffect } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { Chart } from 'react-chartjs-2'; // Import Chart.js or preferred chart library
import { Table, Dropdown, Button, Input } from 'semantic-ui-react'; // Using Semantic UI for quick styling

const Admin = () => {
    // Define states for each section
    const [medicineData, setMedicineData] = useState([]);
    const [expiringMeds, setExpiringMeds] = useState([]);
    const [orderStatus, setOrderStatus] = useState([]);
    const [suppliers, setSuppliers] = useState([]);
    const [salesHistory, setSalesHistory] = useState([]);
    const [allProducts, setAllProducts] = useState([]);

    // Fetch all data on mount
    useEffect(() => {
        fetchData(); // This should contain all API calls to load initial data
    }, []);

    // Example data fetching function
    const fetchData = () => {
        // Fetch medicine quantity data
        // Fetch expiring soon medicines
        // Fetch order status data
        // Fetch suppliers data
        // Fetch sales history
        // Fetch all products
    };

    // Statistics section data for medicine quantities
    const statisticsData = {
        labels: medicineData.map((med) => med.name),
        datasets: [{
            label: 'Quantity',
            data: medicineData.map((med) => med.quantity),
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
        }],
    };

    return (
        <div className="admin-page">
            <h1>Admin Dashboard</h1>
            <Tabs>
                {/* Statistics Section */}
                <TabList>
                    <Tab>Statistics</Tab>
                    <Tab>Expiring Soon</Tab>
                    <Tab>Order Status</Tab>
                    <Tab>Suppliers & Ordering</Tab>
                    <Tab>Sales History</Tab>
                    <Tab>Product Management</Tab>
                </TabList>

                <TabPanel>
                    <h2>Medicine Quantity Statistics</h2>
                    <Chart type="bar" data={statisticsData} options={{ responsive: true }} />
                </TabPanel>

                {/* Expiring Soon Section */}
                <TabPanel>
                    <h2>Expiring Soon</h2>
                    <Table celled>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Medicine</Table.HeaderCell>
                                <Table.HeaderCell>Expiration Date</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {expiringMeds.map((med) => (
                                <Table.Row key={med.id}>
                                    <Table.Cell>{med.name}</Table.Cell>
                                    <Table.Cell>{med.expiryDate}</Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                </TabPanel>

                {/* Order Status Section */}
                <TabPanel>
                    <h2>Order Status</h2>
                    <Table celled>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Order ID</Table.HeaderCell>
                                <Table.HeaderCell>Status</Table.HeaderCell>
                                <Table.HeaderCell>Update Status</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {orderStatus.map((order) => (
                                <Table.Row key={order.id}>
                                    <Table.Cell>{order.id}</Table.Cell>
                                    <Table.Cell>{order.status}</Table.Cell>
                                    <Table.Cell>
                                        <Dropdown
                                            placeholder="Update Status"
                                            selection
                                            options={[
                                                { key: 'processing', text: 'Processing', value: 'Processing' },
                                                { key: 'shipped', text: 'Shipped', value: 'Shipped' },
                                                { key: 'delivered', text: 'Delivered', value: 'Delivered' },
                                            ]}
                                            onChange={(e, { value }) => handleUpdateOrderStatus(order.id, value)}
                                        />
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                </TabPanel>

                {/* Suppliers & Ordering Section */}
                <TabPanel>
                    <h2>Suppliers & Ordering</h2>
                    <Table celled>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Supplier Name</Table.HeaderCell>
                                <Table.HeaderCell>Contact</Table.HeaderCell>
                                <Table.HeaderCell>Place Order</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {suppliers.map((supplier) => (
                                <Table.Row key={supplier.id}>
                                    <Table.Cell>{supplier.name}</Table.Cell>
                                    <Table.Cell>{supplier.contact}</Table.Cell>
                                    <Table.Cell>
                                        <Button onClick={() => handlePlaceOrder(supplier.id)}>Place Order</Button>
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                </TabPanel>

                {/* Sales History Section */}
                <TabPanel>
                    <h2>Sales History</h2>
                    <Table celled>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Date</Table.HeaderCell>
                                <Table.HeaderCell>Medicine</Table.HeaderCell>
                                <Table.HeaderCell>Quantity Sold</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {salesHistory.map((sale) => (
                                <Table.Row key={sale.id}>
                                    <Table.Cell>{sale.date}</Table.Cell>
                                    <Table.Cell>{sale.medicine}</Table.Cell>
                                    <Table.Cell>{sale.quantity}</Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                </TabPanel>

                {/* Product Management Section */}
                <TabPanel>
                    <h2>Product Management</h2>
                    <Table celled>
                        <Table.Header>
                            <Table.Row>
                                <Table.HeaderCell>Medicine</Table.HeaderCell>
                                <Table.HeaderCell>Quantity</Table.HeaderCell>
                                <Table.HeaderCell>Availability</Table.HeaderCell>
                                <Table.HeaderCell>Actions</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            {allProducts.map((product) => (
                                <Table.Row key={product.id}>
                                    <Table.Cell>{product.name}</Table.Cell>
                                    <Table.Cell>
                                        <Input
                                            type="number"
                                            value={product.quantity}
                                            onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                                        />
                                    </Table.Cell>
                                    <Table.Cell>{product.isAvailable ? 'Available' : 'Not Available'}</Table.Cell>
                                    <Table.Cell>
                                        <Button onClick={() => toggleAvailability(product.id)}>
                                            {product.isAvailable ? 'Make Unavailable' : 'Make Available'}
                                        </Button>
                                    </Table.Cell>
                                </Table.Row>
                            ))}
                        </Table.Body>
                    </Table>
                </TabPanel>
            </Tabs>
        </div>
    );
};

export default Admin;
