import React, { useState, useEffect } from 'react';
import Bounce from 'react-reveal/Bounce';
import OrderCard from '../components/Order/OrderCard';
import useOrder from '../hooks/useOrder';
import { addDoc, collection, doc, updateDoc, arrayUnion, setDoc, getDoc, getDocs, query, where } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import useAuth from '../hooks/useAuth';
import { getFirestore } from 'firebase/firestore';


import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import Navbar from '../components/Navbar/Navbar';

const OrderScreen = () => {
    const { orders } = useOrder();
    const [deliveryTiming, setDeliveryTiming] = useState('one-time');
    const [deliveryTimeRange, setDeliveryTimeRange] = useState('None');
    const [recurringOption, setRecurringOption] = useState('None');
    const [paymentType, setPaymentType] = useState('cash');
    const [totalPrice, setTotalPrice] = useState(0);
    const [qrCodeUrl, setQrCodeUrl] = useState(null); // QR code URL state
    const [paymentScreenshot, setPaymentScreenshot] = useState(null); // Payment screenshot state
    const gstRate = 0.05;
    const { user } = useAuth();
    const db = getFirestore();
    const storage = getStorage();
    const history = useHistory();

    useEffect(() => {
        const price = orders.reduce((acc, order) => {
            return acc + (order.price * order.quantity);
        }, 0);
        setTotalPrice(price + price * gstRate);
    }, [orders]);

    useEffect(() => {
        // Fetch UPI QR Code URL from Firebase Storage
        const fetchQrCode = async () => {
            const qrRef = ref(storage, 'path_to_qr_code/upi_qr.png');
            const qrUrl = await getDownloadURL(qrRef);
            setQrCodeUrl(qrUrl);
        };

        if (paymentType === 'upi') {
            fetchQrCode();
        }
    }, [paymentType, storage]);

    const handleScreenshotUpload = (e) => {
        setPaymentScreenshot(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        const orderDetails = {
            user_id: user?.uid || 'unknown',
            items: orders.map(item => ({
                product_id: String(item.id),
                name: item.title,
                quantity: item.quantity,
                price_per_unit: item.price,
                total_price: item.price * item.quantity
            })),
            total_price: totalPrice || 0,
            gst: totalPrice * gstRate || 0,
            delivery_timing: deliveryTiming || 'None',
            delivery_time_range: deliveryTimeRange || 'None',
            recurring_option: recurringOption || 'None',
            payment_type: paymentType || 'None',
            timestamp: new Date(),
            status: 'pending'
        };
    
        console.log("Order Details:", orderDetails);
    
        try {
            const order_id = `order_${new Date().getTime()}`;
            const orderRef = doc(db, 'orders', order_id);

            for (const item of orders) {
                const productQuery = query(
                    collection(db, 'products'),
                    where('id', '==', item.id)
                );

                const productSnapshot = await getDocs(productQuery);

                if (!productSnapshot.empty) {
                    const productDoc = productSnapshot.docs[0];
                    const currentQuantity = productDoc.data().quantity;
                    const newQuantity = currentQuantity - item.quantity;

                    await updateDoc(productDoc.ref, {
                        quantity: newQuantity >= 0 ? newQuantity : 0
                    });
                } else {
                    console.warn(`Product with ID ${item.id} does not exist.`);
                }
            }

            // Save payment screenshot if UPI was selected
            let screenshotUrl = null;
            if (paymentType === 'upi' && paymentScreenshot) {
                const userProfileDoc = await getDoc(doc(db, 'user_profile', user.uid));
                const mobileNumber = userProfileDoc.data().phone;

                const screenshotRef = ref(storage, `payments/${mobileNumber}_${order_id}.jpg`);
                await uploadBytes(screenshotRef, paymentScreenshot);
                screenshotUrl = await getDownloadURL(screenshotRef);
                orderDetails.payment_screenshot = screenshotUrl;
            }

            // Set order document in 'orders' collection
            await setDoc(orderRef, orderDetails);

            const userRef = doc(db, 'user_profile', String(user.uid));
            await updateDoc(userRef, {
                orders: arrayUnion(order_id)
            });

            swal("Order Placed!", "Your order has been placed successfully!", "success");
            history.push('/');
        } catch (error) {
            console.error("Error placing order:", error);
        }
    };

    const isFormValid = deliveryTiming !== 'None' && deliveryTimeRange !== 'None' && paymentType !== 'None';

    return (
        <>
            <Navbar />
            <section className="max-w-screen-xl py-24 mx-auto px-6">
                {orders.length === 0 ? (
                    <div className="h-screen">
                        <h1 className="text-3xl poppins text-center text-blue-600">Cart is Empty!</h1>
                    </div>
                ) : (
                    <>
                        <Bounce left>
                            <div className="flex flex-col items-center space-x-2 pb-8">
                                <h1 className="text-gray-700 poppins text-3xl">All <span className="text-blue-600 font-semibold select-none">Orders</span></h1>
                                <div className="bg-blue-600 flex items-center justify-center w-16 h-1 mt-2 rounded-full"></div>
                            </div>
                        </Bounce>
                        <div className="flex justify-center">
                            <div className="flex flex-col space-y-4">
                                {orders.map(item => (
                                    <OrderCard key={item.id} {...item} />
                                ))}
                            </div>
                        </div>

                        <div className="mt-12">
                            <h2 className="text-2xl font-semibold mb-4">Delivery Details</h2>
                            <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
                                {/* Existing form fields */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Payment Type</label>
                                    <select 
                                        value={paymentType}
                                        onChange={(e) => setPaymentType(e.target.value)}
                                        className="mt-1 block w-full border-gray-300 rounded-md"
                                    >
                                        <option value="None">Select Payment</option>
                                        <option value="cash">Cash</option>
                                        <option value="upi">UPI</option>
                                    </select>
                                </div>

                                {/* Show QR code and file input for UPI payment */}
                                {paymentType === 'upi' && (
                                    <>
                                        <div className="mt-4">
                                            <h3 className="text-lg font-semibold">Scan UPI QR Code to Pay</h3>
                                            {qrCodeUrl && <img src={qrCodeUrl} alt="UPI QR Code" className="w-48 h-48 mt-4" />}
                                        </div>
                                        <div className="mt-4">
                                            <label className="block text-sm font-medium text-gray-700">Upload Payment Screenshot</label>
                                            <input 
                                                type="file" 
                                                onChange={handleScreenshotUpload} 
                                                className="mt-1 block w-full border-gray-300 rounded-md"
                                                required={paymentType === 'upi'}
                                            />
                                        </div>
                                    </>
                                )}

                                <div className="mt-4">
                                    <h3 className="text-lg font-semibold">Total Price (including GST): â‚¹{totalPrice.toFixed(2)}</h3>
                                </div>

                                <button 
                                    type="submit"
                                    className={`mt-4 bg-blue-600 text-white py-2 px-4 rounded-md ${!isFormValid ? 'opacity-50 cursor-not-allowed' : ''}`}
                                    disabled={!isFormValid}
                                >
                                    Place Order
                                </button>
                            </form>
                        </div>
                    </>
                )}
            </section>
        </>
    );
};

export default OrderScreen;
